package com.letchic.model;import javax.persistence.*;@Entity@Table(name = "appeal")public class Appeal {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long appealid;    private long userid;    private String message;    private String date;    public long getAppealid() {        return appealid;    }    public void setAppealid(long appealid) {        this.appealid = appealid;    }    public long getUserid() {        return userid;    }    public void setUserid(long userid) {        this.userid = userid;    }    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }    public String getDate() {        return date;    }    public void setDate(String date) {        this.date = date;    }}