package com.letchic.model;import javax.persistence.*;@Entity@Table(name = "cards")public class Card {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private String code;    private String cardholder;    private String carddate;    private String cvv;    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;    }    public String getCardholder() {        return cardholder;    }    public void setCardholder(String cardholder) {        this.cardholder = cardholder;    }    public String getCarddate() {        return carddate;    }    public void setCarddate(String carddate) {        this.carddate = carddate;    }    public String getCvv() {        return cvv;    }    public void setCvv(String cvv) {        this.cvv = cvv;    }    @Override    public String toString() {        return "Card{" +                "code='" + code + '\'' +                ", cardholder='" + cardholder + '\'' +                ", carddate='" + carddate + '\'' +                ", cvv='" + cvv + '\'' +                '}';    }}